     */
    function edit($grado_id)
    {   
        // check if the grado_persona exists before trying to edit it
        $data['grado_persona'] = $this->Grado_persona_model->get_grado_persona($grado_id);
        
        if(isset($data['grado_persona']['grado_id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
