        $grado_persona = $this->Grado_persona_model->get_grado_persona($grado_id);

        // check if the grado_persona exists before trying to delete it
        if(isset($grado_persona['grado_id']))
        {
            $this->Grado_persona_model->delete_grado_persona($grado_id);
